import { useState } from 'react'
import { useWorkoutContext } from '../hooks/useWorkoutContext'  

const WorkoutForm = () => {

    const {dispatch} = useWorkoutContext()

    const [title, setTitle] = useState('')
    const [load, setLoad] = useState('')
    const [reps, setReps] = useState('')
    const [error, setError] = useState(null)
    const [emptyFields, setEmptyFields] = useState([])

    const handleSubmit = async (e) => {
        e.preventDefault() //dont refresh

        const workout = {title, load, reps}

        const response = await fetch('/api/workouts', {
            method: 'POST',
            body: JSON.stringify(workout), //set body of request to jsonified workout object
            headers: {
                'Content-Type': 'application/json'
            }
        })

        const json = await response.json() //get the response

        if(!response.ok) {
            setError(json.error)
            setEmptyFields(json.emptyFields) //keep emptyfields
        }
        if (response.ok) {
            //reset all of the states
            setTitle('')
            setLoad('')
            setReps('')
            setError(null)
            setEmptyFields([]) 
            console.log('New Workout added', json)

            //update context
            dispatch({type: 'CREATE_WORKOUT', payload: json})
        }
    }

  return (
    <form className="create" onSubmit={handleSubmit}>
        <h3>Add a New Workout</h3>

        <label>Exercise Title:</label>
        <input 
        type="text"
        onChange={(e) => setTitle(e.target.value)}
        value={title}
        />

    <label>Load (in kg):</label>
        <input 
        type="number"
        onChange={(e) => setLoad(e.target.value)}
        value={load}
        />

    <label>Reps:</label>
        <input 
        type="number"
        onChange={(e) => setReps(e.target.value)}
        value={reps}
        />

        <button>Add Workout</button>

        {error && <div className='error'>{error}</div>}
    </form>
  )
}

export default WorkoutForm
